package com.suhuamo.init.pojo.dto.user;import lombok.Data;import lombok.NoArgsConstructor;import lombok.experimental.Accessors;import javax.validation.constraints.NotEmpty;import java.io.Serializable;/*** <p>* 用户表 后端->前端数据显示类* </p>** @author suhuamo* @date 2024-03-19*/@Data@NoArgsConstructor@Accessors(chain = true)public class UserAddDTO implements Serializable {    private static final long serialVersionUID = 1L;    /**     * 账号     */    @NotEmpty(message = "账号不能为空")    private String name;    /**     * 密码     */    @NotEmpty(message = "密码不能为空")    private String password;    /**     * 头像地址     */    private String avatar;    /**     * 邮箱     */    private String email;    /**     * 电话号码     */    private String telephone;    /**     * 类型，0-男；1-女     * @with {@link com.suhuamo.init.enums.SexEnum}     */    private Integer sex;}